import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './style.css'

// API 키를 자신의 키로 교체하세요.
const API_KEY = 'YOUR_YOUTUBE_API_KEY';

// 해당 채널의 ID로 대체하세요. 채널 ID는 YouTube API로 가져올 수 있습니다.
const CHANNEL_ID = 'UCuiseongTVCHANNELID'; // 예시: "UCx0-EyycTOI5rPsU1oxu-Rg"

const App = () => {
  const [videos, setVideos] = useState([]);

  useEffect(() => {
    // YouTube Data API를 사용하여 Shorts 영상을 가져오는 함수
    const fetchShortsVideos = async () => {
      try {
        const response = await axios.get('https://www.googleapis.com/youtube/v3/search', {
          params: {
            key: API_KEY,
            channelId: CHANNEL_ID,
            part: 'snippet',
            type: 'video',
            maxResults: 10,
            order: 'date',
            videoDuration: 'short'
          }
        });
        setVideos(response.data.items);
      } catch (error) {
        console.error('Error fetching YouTube shorts videos', error);
      }
    };

    fetchShortsVideos();
  }, []);

  return (
    <div className="App">
      <h1>YouTube Shorts Videos</h1>
      <div className="shorts-container">
        {videos.map((video) => (
          <div key={video.id.videoId} className="shorts-video">
            <iframe
              width="320"
              height="180"
              src={`https://www.youtube.com/embed/${video.id.videoId}`}
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
              title={video.snippet.title}
            ></iframe>
            <p>{video.snippet.title}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
