import React,{useState} from 'react';
import axios from 'axios'
import { useNavigate } from "react-router-dom";
import './style.css';
import { ToastContainer, toast } from 'react-toastify'
import 'react-toastify/dist/ReactToastify.css';


const Login = () => {
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")
  const [showPswd, setshowPswd] = useState(false)
  const [loading, setLoading] = useState(false)
  const navigate = useNavigate();

  const gotoSignuppage = () => {
    navigate("/signup");
};


  const cancelPaste = (e) => {
    e.preventDefault();
  }

  const handleEmailChange = (e) => {
  const regex = /^[A-Za-z0-9@.]*$/;

  if (regex.test(e.target.value)) {
    setEmail(e.target.value);
  }
  };


  const handlePasswordChange = (e) => {
    const inputValue = e.target.value;
    const sanitizedValue = inputValue.replace(/[^a-zA-Z0-9!@#$%^&*()_+{}|:"<>?~`[\];',./\\-]/g, '');
    setPassword(sanitizedValue);
  }
  const changePswd = () =>{
    if (showPswd === true){
      setshowPswd(false)
    }else{
      setshowPswd(true)
    }
  }

  const login = async () => {
    setLoading(true)
    if (email === "" || password ===""){
      toast.error("이메일 또는 비밀번호가 잘못 되었습니다")
      setLoading(false)
      return
    }
    try {
      const { data } = await axios.post(`http://finder.mcv.kr:8080/auth/login`, {
        email,
        password
      })

      localStorage.setItem('accessToken', data.accessToken)
      localStorage.setItem('refreshToken', data.refreshToken)

      navigate('/')
    } catch (e) {
      if (axios.isAxiosError(e)) {
        const code = e.response.data.code
        toast.error("이메일 또는 비밀번호가 잘못 되었습니다")
        if (code === 'USER_NOT_FOUND') {
          toast.error("이메일 또는 비밀번호가 잘못 되었습니다")

          return
        }
      }
    } finally {
      setLoading(false)
    }
  }


  return (
    <div className="login-container">
      <h1>로그인</h1>
      <p className='title'>로그인하고 잃어버린 물건 찾기!</p>
      <input
    type="text"
    onPaste={handleEmailChange}
    onChange={handleEmailChange}
    value={email}
    placeholder="이메일"
  />
      <div className="passwordbox">
        <input type={showPswd ? "text" : "password"} onPaste={cancelPaste} onChange={handlePasswordChange} value={password} maxLength="200" placeholder="비밀번호" />
        <div className="showpassword"  onClick={changePswd}></div>
      </div>
      <button className="login-button" onClick={login} disabled={loading}>로그인</button>
      <p className='noaccount'>계정이 없다면? <span className='signupbutton' onClick={gotoSignuppage}>회원가입하기</span></p>
      <ToastContainer
        position='top-right'
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop={true}
        closeOnClick
        pauseOnFocusLoss
        pauseOnHover={false}
        limit={5}
       />
    </div>
  );
}

export default Login;
